mapper.select:
select * from `excellent_topic`

     and title like concat('%', #{title}, '%')
     and category = #{category}
     and year = #{year}
     and award_info like concat('%', #{awardInfo}, '%')
     and tags like concat('%', #{tags}, '%')
     and image_url = #{imageUrl}

order by year desc, id desc

select * from `excellent_topic`


        and (title like concat('%', #{keywords}, '%') or tags like concat('%', #{keywords}, '%'))


        and category = #{category}


order by year desc, id desc

    limit #{limit}
